// Toggle output each time a message arrives
let fans = context.get('fans') || false;  // get previous state, default false

fans = !fans;              // flip the state
context.set('fans', fans); // remember it

return { payload:{cooler: fans?"on":"off"} };   // send true/false (on/off)

// Toggle output each time a message arrives
let auxLights = context.get('auxLights') || false;  // get previous state, default false

auxLights = !auxLights;              // flip the state
context.set('auxLights', auxLights); // remember it

return { payload:{auxLighting: auxLights?"on":"off"} };   // send true/false (on/off)

if( msg.payload.test ){
    let mainLights = context.get('mainLights');
    mainLights = !mainLights;
    context.set('mainLights', mainLights);
    return { payload:{lighting: mainLights?"on":"off"} }; 
